css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("businessinsider.com")

{

/*text&background*/

html, body, tbody, thead, th, tr, td, blockquote, li, ul, h1, h2, h3, h4, h5, h6, p, form, div#ft, div#hd, #hd div.menu, #hd div.menu-subvert, #hd-below, #doc4, div.box-post div.content, div.comments .inner, div.comments .odd, #right-rail .hbot, #right-rail .htop, #right-rail .box-1, #right-rail .box-1 > .first, #right-rail .right-news, .post .post-sidebar-container, div.post-sidebar-module, div.featured-comments, div.bleachers-comments, div.link-show

{
color: white !important;
background: black !important;
border: black !important;
}


select, button

{
background: black !important;
}

div, span, a

{
background-color: black !important;
}

select, input, span, code, textarea, pre, label, div, dd

{
color: white !important;
}

/*links*/

a

{
color: #0040ff !important;
}

a:visited

{
color: #224375 !important;
}

/*highlight*/

textarea, pre

{
background: #333333 !important;
}

}
