css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("compare.buscape.com.br"), domain("preco2.buscape.com.br") {

.bp_prod_list .product_inner, .bp_prod_list .line_four_inner {
    padding: 0;
}

.bp_prod_list .product_description {
    position:absolute;
    top:50%;
}

#bp_prod_list div {
   background: none repeat scroll 0 0 #ffffff
}

#bp_prod_list img {
    height: 70px
}

ul li div {
    height: 50px
}

.bp_shop_info {
    display: none
}

.bp_mod_price .offer .ft {
    display: none;
}

.bp_mod_ads, .line_four_inner {
    display: none
}

}
