@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lifesitenews.com") {

/*text&background*/

html, body, tbody, thead, th, tr, td, blockquote, li, ul, h1, h2, h3, h4, h5, h6, p, form, footer, div.main-area, #main .user-area, div.content-area, div.category_nav_bar, .content .place, .aside .box, #commentform, .title, #sidebar .block-in .heading, #sidebar .block-in, .aside .block, .aside .block .heading, #sidebar .block .heading, #sidebar .block, #header, .twtr-hd, div.twtr-tweet-wrap, .twtr-ft div, .article, #popup-main

{
color: white !important;
background: black !important;
border: black !important;
}

#sidebar .tabset a

{
background: black !important;
}

/*links*/

a

{
color: #0040ff !important;
}

/*highlight*/

textarea, pre

{
background: #333333 !important;
}

}
