css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("9gag.com") {

div#top-ad-tray, div.ad-block, div.section-1 {display: none !important;}
div.side-dock {display: none !important;}
#head-bar {max-width: 100% !important; float: none !important;}
div#headbar-wrap {width: 100% !important; float: none !important; min-width: 100% !important;}
#block-content {max-width: 100% !important;}
#main {float: none !important; width: 100% !important;}
.spread-bar-wrap, .secondary-menu {display: none !important;}
.wrap .info {display: none !important;}
div#view-controller {display: none !important;}
div#footer {display: none !important;}
#content {padding-top: 0px !important;}
body#page-landing {min-width: 100% !important;}
div#container {width: 100% !important; margin: 50px 0 0 0 !important;}
#head-bar ul.main-menu li a {padding: 20px 16px !important;}
}
