css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("cnet.com"),
domain("cnet.com.au"),
domain("cnetfrance.fr"),
domain("cnetnews.com.cn"),
domain("cnet.co.uk"),
domain("cnet.de"),
domain("cnetstatic.com")
{

/*text&background*/

html, body, tbody, thead, th, tr, td, blockquote, li, ul, h1, h2, h3, h4, h5, h6, p, form, footer, .leaderboard, .leaderBoard, #leaderboard, #leaderBoard, .rb_skin, div#rb_content, header#rbHeader, nav#primaryNav, #readersChoice .tabContent, #contentAux .about, .blogTopics, #primaryNav, #subNav, #rbSkin, #main, #rbWrap, #primaryNavBar > li.expandable .menuWrapper .nav, #edition .menuWrapper .nav, .dfllDoorPromo, div.content, div#rb_shell, div#rb_wrap, div.module, div.latestReviews, div.userReviews, #contentBody .post .postByline, #contentBody .editorBio, #recentRelated .relatedPosts dd:first-of-type, #recentRelated .recentPosts dd:first-of-type, .sideScrollerWrapper .sideScrollerPromo .scrollingArrows, #cnetOnTheGo, #rcTabs, .radialHighlight460, .catNav dd:nth-of-type(1), .softwareFinder_2, #postComments .commentwrapper, #postComments .commentwrapper dl, .inline-slideshow .scrollingArrows, #contentBody .postBody.first

{
color: white !important;
background: black !important;
border: black !important;
box-shadow: 0 6px 10px #000000 inset !important;
}

.ui-tabs-nav li a, #primaryNavBar > li.expandable .menuWrapper nav, #customPrimaryNavBar > li.expandable .menuWrapper nav, .noResultsWrap

{
background: black !important;
}

/*links*/

a

{
color: #0040ff !important;
}

/*highlight*/

textarea, pre, .searchBoxWrap, .ui-tabs-nav li.ui-tabs-selected a, .ui-tabs-nav li.ui-tabs-selected a:hover, #primaryNavBar > li.expandable .menuWrapper li a:hover, #edition .menuWrapper li a:hover, .catNav dd > a:hover

{
background: #333333 !important;
}

}
