css:
@-moz-document url-prefix("https://www2.rit.edu/BatBOX/timeTrack/addProjectHours.jsp"), url-prefix("https://www2.rit.edu/BatBOX/timeTrack/index.jsp") {
td[bgcolor="AAAAAA"], td[bgcolor="#AAAAAA"] {
  background-color: transparent;
}
form[name="modifyProject"] table, form[name="Hours"] table {
border-collapse:collapse !important;
border:none !important;
background-color: #fafafa !important;
margin: 16px 0 !important;
font-size: 11px !important;
font-family: verdana !important;
}
form[name="modifyProject"] td, form[name="Hours"] td {
padding:10px 8px !important;
border-bottom: none !important;
border-left:none !important;
border-right:none !important;
}
form[name="modifyProject"] tr:first-child td, form[name="Hours"] tr:first-child td {
padding:10px !important;
font-weight:bold !important;
text-align:center;
color:#fff;
border-top:1px solid #444 !important;
background: #333;
}
form[name="modifyProject"] tr td:first-child, form[name="Hours"] tr td:first-child {
border-left:1px solid #ddd !important;
}
form[name="modifyProject"] tr td:last-child, form[name="Hours"] tr td:last-child {
border-right:1px solid #ddd !important;
}
form[name="Hours"] table input {
border:1px solid #aaa !important;
padding: 4px !important;
}
form[name="modifyProject"] tr:first-child td:first-child, form[name="Hours"] tr:first-child td:first-child {
border-left:1px solid #444 !important;
}
form[name="modifyProject"] tr:first-child td:last-child, form[name="Hours"] tr:first-child td:last-child {
border-right:1px solid #444 !important;
}
form[name="modifyProject"] tr:last-child td, form[name="Hours"] tr:last-child td {
border-bottom: 1px solid #ddd !important;
}
form[name="Hours"] tr td:last-child input {
    width: 400px;
}
form[name="Hours"] input[type="button"] {
position:fixed;
bottom:-2px;
left:0px;
width:100% !important;
height:60px !important;
display:block !important;
-webkit-appearance:button;
font-size:24px !important;
}
body > center > table {
margin-bottom:40px;
}
ul > li {
      font-size: .85em;
    line-height: 1.7em;
}
ul > li a {
  text-decoration: none;  
}
ul > li a.remove {
  text-decoration: underline;  
}
}
