css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tmz.com")

{

/*text&background*/

html, body, tbody, thead, th, tr, td, blockquote, h1, h2, h3, h4, h5, h6, p, form, footer, #page-content, #main, #outer-main, div.swipe, span.hf1, span.hf2-case, span.hf3-case, div.inner, div.scroller, div#footer, div.primary-content, .rr-box, li.clearfix, #page-wrap

{
color: white !important;
background: black !important;
}

select, button

{
background: black !important;
}

div, span, a

{
background-color: black !important;
}

select, input, span, code, textarea, pre, label, div, dd, option

{
color: white !important;
}


/*links*/

a

{
color: #0040ff !important;
}

a:visited

{
color: #224375 !important;
}

/*highlight*/

textarea, pre, input, code

{
background: #333333 !important;
}

}
