css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("infowars.com"),
domain("prisonplanet.com"),
domain("prisonplanet.tv")
{

html, body, tbody, thead, th, tr, td, blockquote, li, ul, h1, h2, h3, h4, h5, h6, p, form, footer, div#nBody, div#nBodyLeft, div#nFeaturedStoriesContent, div.nSectionContent, div#nContainer, div#nFooter, .boxheader, .sidebarbox, #nFeaturedStories, ol.commentlist, .catbg, div.titlebg2

{
background: black !important;
border: black !important;
color: white !important;
}

div#nBodyLeft div

{
background-color: black !important;
}

.quote, select

{
color: white !important;
}

/*links*/

a

{
color:#0040ff!important;
}

/*highlight*/

textarea, pre, .quote

{
background: #333333 !important;
}

}
