css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("blog.esuteru.com") {

  nav#headMenu,
  div#logoArea > :not(h1),
  div#headline,
  div#top_tag_list,
  div[class^="google-user-ad"],
  aside#right_col,
  hr#page_navi_bar,
  nav#footMenu,
  footer
  {display: none !important;}
  
  header,
  header > div.wrap.clearfix{
    padding: 0px !important;
    height: 60px !important;
    width: 800px !important;
    margin: 0px auto !important;
  }
  
  div#content{
    width: 798px !important;
    margin: 0px auto !important;
    border: 1px solid #DC7094 !important;
  }
  
  div#logoArea > h1{
    margin: 0px !important;
  }
  
  div#page_navi{
    margin: 0px !important;
  }
  ul.index-navigator{
    align-content: center;
    width: 728px !important;
    margin: 10px auto !important;
  }
  
  
}



@-moz-document url-prefix("http://blog.esuteru.com/archives/"){
  
  /*div.moreBody > br,
  div#comment,
  div.moreBody > blockquote:last-of-type,*/
  div[class^="amazon"],
  a[href^="http://www.amazon.co.jp/exec/"],
  div.adsense_rss,
  div.ad300_250x2,
  div.ad300_250x2 + div,
  div.bodySocial
  {display: none !important;}
  
  
  
  
}
