css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("npr.org")

{

/*text&background*/

html, body, tbody, thead, th, tr, td, blockquote, li, ul, h1, h2, h3, h4, h5, h6, p, form, footer, div.left, div.right, div#wrapper_main, #footer .listbg, #footer .col, #header, #todaysdate, div.featured, div.colcontent, div.duration, div.avcontent

{
color: white !important;
background: black !important;
}

.hpfeatured

{
background: black !important;
}

/*links*/

a

{
color: #0040ff !important;
}

/*highlight*/

textarea, pre

{
background: #333333 !important;
}

}
