css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://www.google.),
url-prefix(https://www.google.),
domain(google.com) {

/*********************************************/
/*total bar*/

#gb {height: 71px !important;}

/*********************************************/
/*black top bar*/

#gbx3 {height: 29px !important;}
#gbzc .gbt {line-height: 27px !important;}

/*********************************************/
/*the gray bar*/

/* adjust the height of gray bar */
#gbx1 { height: 41px !important; }

/*adjust the positions of buttons on the gray bar*/
#gbq2 {padding-top: 5px !important;} /*search box*/
#gbu {padding-top: 5px !important;} /*user information*/
#gbqlw {height: 45px !important;} /*the google icon*/

/*********************************************/

/* result number float left */
#resultStats {float: left !important;}

/*Search box text alignment*/
.gsib_a {
    padding-top:1px !important;
    padding-bottom:0px !important;
    padding-left:5px !important;
}

/*hide share link*/
.pplsrsl { display: none !important; }

/*hide search the web*/
.lnsep,
LI[id^="lr_"] { display: none !important; }

/*hide the quick view icon
.pplsrsl,
.vspib {display: none !important;} */

/*hide the space between top bar and search results*/
#subform_ctrl,
#ucs { display: none !important; }

#gbx1
,#gbq
,#gbu
,#gb #gbq
,#gb #gbu { top: 30px !important; }

}

/*************** for google images ***************/

@-moz-document domain(images.google.com) {
/*google images search box*/
#qbp {top: -20px !important;}
}

/*************** for google plus ***************/

@-moz-document domain(plus.google.com) {

.Dob {height: 72px !important;} /*total top bar height*/

/*circle bar*/
.XI.Lg,
.pu {
    height: 40px !important;
    line-height: 37px !important;
}

/*the line that seperates the circle bar and main content*/
.wl {margin-top: 40px !important;}

/*fix the bug that "more" button is bigger than the other buttons*/
.GM .a-f-e {height: 27px !important;}

}

/*************** for google play ***************/
@-moz-document domain(play.google.com) {

/*fix the height of top bar*/
#gb {height: 30px !important;}

}
