css:
@-moz-document domain("epicfail.com") {
img[src="http://www.epicfail.com/wp-content/themes/epicfail/images/logo.png"] {height:40px !important;}
a#logo {margin-top:5px !important;}
div.pri {height:40px !important;}
div#content {width:auto !important; float:none !important;}
.post {margin-bottom:25px !important;}
table {height:15px !important;}
div#footer {min-height:0 !important;}
img[src="/images/facebook.png"], img[src="/images/twitter.png"] {display:none !important;}
div.wp-pagenavi {width:auto !important;}
div.entry {margin: 15px 0 0 0 !important;}

div#adv728, div#sidebar, div#featured.ui-tabs-nav, iframe, tbody, div.failit, b > h2:nth-of-type(1), br {display:none !important;}
}
