css:
@-moz-document url-prefix("http://www.linux.org.ru/wiki/") {
body { 
  background: #2E3436;
  color: #BABDB6;
}

h1, h2, h3, h4, h5 {
  color: #BABDB6;
}


.portlet {
  background: #272C2D;
  border: 1px solid #272C2D;
  border-radius: 1em;
}

a {
  text-decoration: none !important;
  color: #729FCF;
}

a:hover {
  border-bottom: 0px !important;
}

.tab-menu li {
  color: #AAAAAA !important;
  background-color:  #272C2D !important;
  border: 1px solid #171B2D !important;
}

ul#tab_menu li.active {
  border-bottom-color: #272C2D !important;
}

#tab_menu {
  margin-left: 1em !important;
}

.tab-menu  a {
 color: #FCAF3E;
}

#user-menu a {
 color: #E9B96E;
 font-size: 1.2em;
 text-decoration: none;
}

#user-menu {
 float: none !important;
 margin-bottom: 10px !important;
 background-color: #272C2D;
 padding-bottom: 1em;
 padding-top: 1em;
 width: 100%;
 border-radius: 10px;
}

#user-menu {
 margin-bottom: 20px;
}

#contents {
  background: #272C2D !important;
  border: 1px solid #272C2D;
  border-radius: 1em;
  color: #BABDB6 !important;
  list-style-type: disc !important;
}

table {
background-color: #272C2D !important;
border-color: #BABDB6 !important;
}

pre {
background-color: #2E3436 !important;
border-radius: 1em;
border: 0px !important;
color: white;
}
}
