css:
@-moz-document url-prefix("http://msdn.microsoft.com/zh-cn"), domain("technet.microsoft.com") {
*
{ font-family: Verdana, "Microsoft Yahei", "Meiryo" !important; }
.topic{ font-size:10pt !important; line-height:12pt !important; }
.LW_CollapsibleArea_Title { line-height: 18pt !important; }
#popup0{ visibility: hidden; display: none; top: -99999em !important; }
pre { font-family: Consolas !important}
}

@-moz-document domain("blogs.msdn.com") {
div.postsub { font-size:12pt !important; line-height:16pt !important; font-family: Consolas, "Microsoft Yahei", "Meiryo";}
}

@-moz-document url-prefix("http://msdn.microsoft.com/ja-jp") {
*
{ font-family: Verdana, "Meiryo" !important; }
.topic{ font-size:10pt !important; line-height:12pt !important; }
.LW_CollapsibleArea_Title { line-height: 18pt !important; }
#popup0, .MTPopup, .popup-main, .popup-inner{ visibility: hidden; display: none; top: -99999em !important; }
pre { font-family: Consolas !important}
}
