css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("4chan.org"),
domain("7chan.org"),
domain("imagechan.com"),
domain("desuchan.net"),
domain("megachan.net"),
domain("wakachan.org"),
domain("iichan.net"),
domain("iiichan.net"),
domain("zerochan.net"),
domain("4-ch.net"),
domain("topchan.info"),
domain("420chan.org"),
domain("gurochan.net"),
domain("1chan.net"),
domain("2kochan.com"),
domain("neechan.org"),
domain("petichan.org"),
domain("teamtao.com"),
domain("sovietrussia.org"),
domain("macrochan.org"),
domain("c3.cx"),
domain("deadgods.net"),
domain("secchan.net")

{

/*text&background*/

html, body, tbody, thead, th, tr, td, blockquote, li, ul, h2, h3, h4, h5, h6, font, strong, p, form, footer

{
color: white !important;
background: black !important;
}

select, button

{
background: black !important;
}

div, span

{
background-color: black !important;
}

select, input, span, code, textarea, pre, label, div, dd, option, button

{
color: white !important;
}


/*links*/

a

{
color: #0040ff !important;
}

a:visited

{
color: #224375 !important;
}

/*highlight*/

textarea, pre, input, code

{
background: #333333 !important;
}

}
