css:
@-moz-document domain("www.adelto.co.uk") {
body {font-family:arial!important;}
img { border:none!important; }

.vertical-ad, .horizontal-ad, .post-social, .social, .side-social, .side-facebook, #copyright .float-left, #menu-item-8991 img, #follow_us, .cf, footer, .stripe, #disqus_thread, .share_text, #share_social, .authorsure-author-box {display:none!important;}
#header #sticky {height: 15px!important; background: black!important; padding: 5px!important; margin-top: 10px!important; width: 900px!important; opacity: 0.85!important; position: absolute!important;}
#header #sticky #top-right-nav li a {border-right: 1px solid #555!important; height: 15px!important;}
#menu-item-8991 a {border: none!important;}

.wp-pagenavi span.current {border-color: #0075B8!important; font-weight:normal!important;}
.wp-pagenavi a:hover {border-color: #BA0000!important; text-decoration: underline!important;}
}

@-moz-document domain("home-designing.com") {
#top-nav, .right_posts,.ad, #newsletter, #follow-us, .post-social, .likes, #two_col, .single_left, .sidebar-right { display:none!important; }

.left_posts { width: 100%!important; }
}
