css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.seriadosonlinehd.com") {

#cboxOverlay, #colorbox, a[href^="http://www.mundocinetv.com"], div[style="font-size:30px; text-align:center;"] {display:none!important}

#titulo {background:#222;}
.entry {background:#F1F1F1; padding:5px!important;}
.entry:hover {background:#EEE9E9;}
.post {width:176px!important;}
#sidebar .widgettitle {width:160px; background:#222!important; height:20px!important; padding:7px 20px 2px 10px!important;}
#sidctn {padding:10px 15px 10px 10px!important;}
#sidebar {width:200px!important; margin-right:15px;}

.page-numbers {background:none!important; color:#777!important;}

ul.align-center {text-align:left!important;}
ul.page-bright li {border:1px solid #BFBFBF!important; padding:3px 5px!important;}
ul.shape-round li a {border:none!important; padding:0px!important;}
ul.shape-round li:hover {border-color: #BA0000!important; text-decoration: underline!important;}
ul.shape-round li.first_last_page {border-color: #0075B8!important; }
ul.shape-round li.first_last_page a {background:none!important;}

li span.current {padding:0px!important; text-decoration:underline!important;}
#commentlist2 li {background:#f2f2f2; border-radius:none!important;}

#custom>div[align="center"] {display:none!important;}

}
