css:
@-moz-document domain("twitter.com") {
.global-nav .container {width: 650px;}
.flex-module {display:none}
.profile-summary {display:block}
.module {border:0}
.dashboard {position: fixed;left: 12px;top: 55px;width:190px;z-index:20;opacity:0.7}
.dashboard:hover {width:302px;opacity:1}
.dashboard .js-mini-profile-stats-container, .dashboard .tweet-box {display:none}
.dashboard:hover .js-mini-profile-stats-container, .dashboard:hover .tweet-box {display:block}
.wrapper, .wrapper-narrow, .wrapper-permalink {width:600px}
.content-main {width: 600px}
.js-stream-title {display:none}
.content-header .header-inner {min-height:0px}
.module > .tweet-box:after {display:block;color:#666;border-top:1px solid #ccc;line-height:140%;white-space:pre;padding-top:10px;margin-top:10px;content:"KEYBOARD SHORTCUTS \a N - new tweet \a J - next tweet \a K - prev tweet \a F - favorite \a R - reply \a T - retweet \a M - direct msg \a Enter - tweet details \a L - close all open tweets \a . - load new tweets \a / - search \a \a KEYBOARD MENU SHORTCUTS \a G H - home \a G C - connect \a G R - mentions \a G P - profile \a G F - favorites \a G M - messages \a G U - go to user"}
}
