css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("facebook.com"), domain("zynga.com"), url-prefix("http://zynga"), url-prefix("https://zynga"), domain("akamaihd.net")
{

/* Increase game window */
/* (Change the height value to suit your needs) */
*[id*="flashapp"]{
height:1000px !important;
position:relative !important;
top:0px;
z-index:2;
}

/* other size tweaks */
#tabs ul{
padding: 0px !important;
margin: 0px !important;
}

/* remove junk stuff */
#pagelet_ego_pane, #rightCol, #storyBar, #progressSteps, #zbar{
display:none !important;
}
.noticebox, .msotd_container{
display:none !important;
}
*[src*="facebook.com/plugins/like.php"]{
display:none !important;
}
.msotd_text, .msotd_close_button{
display:none !important;
}
.like_box{
display:none !important;
}
#zap-banner{
display:none !important;
}

}
