css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("discovery.com")

{

/*text&background*/

html, body, tbody, thead, th, tr, td, blockquote, li, ul, h1, h2, h3, h4, h5, h6, font, strong, p, form, footer, fieldset, .body, .post .description, .panel, #container-left-column, #container-wide-left-column, .dropdown .title, .dropdown-menu-topic, .dropdown .featured, .dropdown .lists

{
color: white !important;
background: black !important;
}

select, button, header, #tophat

{
background: black !important;
}

div, span

{
background-color: black !important;
}

select, input, span, code, textarea, pre, label, div, dd, option

{
color: white !important;
}


/*links*/

a

{
color: #0040ff !important;
}

a:visited

{
color: #224375 !important;
}

/*highlight*/

textarea, pre, input, code

{
background: #333333 !important;
}

}
