css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wunderground.com") {
  #bannerTop, #logo,
  #favoritesBar, #optionBar, #featureMenu,
  #makeSticker,
  #postShare,
  #postShare + td,
  #postShare + td + td,
  #postShare + td + td + td,
  #postShare + td + td + td + td,
  #postShare + td + td + td + td + td,
  #innerContent > .switch336,
  #layerCommunity,
  #layerTravel,
  #footer {
    display: none;
  }

  #recentCities {
    position: absolute;
    top: 0.3em;
    right: 4em;
    background: #fff !important;
    padding: 4px 8px;
    border-radius: 4px;
  }
  #rfUpdate {
    width: 100%;
  }
  #pageHeader {
    background: #eee !important;
  }
  #pageHeader,
  #navContent {
    height: 48px !important;
  }
}
