@-moz-document domain("www.nationalreview.com") {

html.js body table#content tbody tr td div#main table.article tbody tr td.content{
background-color:#E0DFDB!important;
border:1px solid #333!important;
}
html.js body table#content tbody tr td div#main table.article tbody tr td.content div#blog_text table tbody tr td div.blog_news div#article_text.article_text div#resizetext div#article_text.article_text p, html.js body table#content tbody tr td div#main table.article tbody tr td.content div#blog_text table tbody tr td div.blog_news div#article_text.article_text div#resizetext div#article_text.article_text {
