css:
@-moz-document domain("trello.com") {

body, input, select, textarea {
  font: 18px/20px Arial;
  color: black;
}

a {
  color: black;
}



.button-link {
  font-size: 17px;
  line-height: 19px;
}

.list-title h2 {
  font-size: 19px;
  margin-top: 1px;
  min-height: 22px;
}

.list-card-title {
  font: 18px/20px Arial;

}

.list-card:hover {
  background-color: #F8EECC;
}

}
