css:
@-moz-document domain("vkontakte.ru"), domain("vk.com") {

div.wall_text div.page_media_thumb>a.video>img { height: 120px !important; width: 159px !important; }
div.wall_text iframe#video_player, div.wall_text embed#video_player, div.wall_text div.video_box_wrap[style] { height: 240px !important; width: 320px !important; }
div.wall_text div.page_media_thumb>div[style] { height: auto !important; width: auto !important; }
div.wall_text div.page_media_thumb>a.photo>img { max-width: 159px !important; }
.wall_module .post_thumbs_num1 .page_media_thumb1 img, .wall_module .post_thumbs_num3 .page_media_thumb1 img, .wall_module .post_thumbs_num4 .page_media_thumb1 img, .wall_module .post_thumbs_num2 .page_media_thumb1 img, .wall_module .post_thumbs_num2 .page_media_thumb2 img { max-height: 180px !important; }
div.wall_text div.video_play_inline { top: 37px !important; left: 57px !important; }
div.feed_row div.wall_text div.video_box_wrap[style] { height: auto !important; width: auto !important; }
div.feed_row div.wall_text embed#video_player, div.feed_row div.wall_text iframe#video_player { height: 360px !important; width: 480px !important; }
}
