css:
@-moz-document domain("livejournal.com") {
.b-leaf-header, .b-massaction  {background:#bbddff !important;}
.b-leaf-meta, .b-leaf-createdtime{color:#000 !important;}
.b-xylem-first {background:none !important;}
.b-massaction{-moz-box-shadow: none !important; -webkit-box-shadow: none !important; box-shadow:none !important;}
/*Comment Box*/
.b-watering-wrapper{-moz-box-shadow: none !important; -webkit-box-shadow: none !important; box-shadow:none !important;  background:#ebf5ff !important; -moz-border-radius: 0 !important; border-radius: 0 !important;}
.b-singlepost .standout .container{background:#bbddff !important;}
/*numbered pages*/
.b-pager-pages  {background:#ebf5ff !important;-moz-border-radius: 0 !important; border-radius: 0 !important;}
.b-pager-page-active {background:white !important; color:#000 !important;-moz-box-shadow: none !important; -webkit-box-shadow: none !important; box-shadow:none !important;}
/*arrow*/
.b-pager-pages-after, .b-pager-pages::after{border:none !important;}
/*reply button and post comment button*/
.b-singlepost .b-leaf-actions .active a:link, .b-singlepost .b-leaf-actions .active a:visited, .b-singlepost .b-leaf-actions .active a:hover, .b-singlepost .b-leaf-actions .active a:active, .b-addcomment-active:link, .b-addcomment-active:visited, .b-addcomment-active:hover, .b-addcomment-active:active, .b-xylem.active .b-addcomment:link, .b-xylem.active .b-addcomment:visited, .b-xylem.active .b-addcomment:hover, .b-xylem.active .b-addcomment:active {-moz-box-shadow: none !important; -webkit-box-shadow: none !important; box-shadow:none !important;  background:#3863A0 !important; -moz-border-radius: 0 !important; border-radius: 0 !important;}
/*submit button*/
.b-ljbutton-submit {background:none !important;}
}
