css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("nypost.com")

{

/*text&background*/

html, body, tbody, thead, th, tr, td, blockquote, li, ul, h1, h2, h3, h4, h5, h6, font, strong, p, form, footer

{
color: white !important;
background: black !important;
}

select, button, #hat_wrap, #footer_outer_wrap, #footer_bottom, #footer_top, .tpl_1, .fatheader_rollover_wrap, .fatheader_rollover, #fat_header.closed .section, #post_ten_long .post_ten_data_body, .rounded_rt_3d .rounded_head, .rounded_rt_3d .rounded_foot, .widget .widget_head, .widget .widget_foot

{
background: black !important;
}

div, span, a

{
background-color: black !important;
}

legend, select, input, span, code, textarea, pre, label, div, dd

{
color: white !important;
}


/*links*/

a

{
color: #0040ff !important;
}

a:visited

{
color: #224375 !important;
}

/*highlight*/

textarea, pre, input, code

{
background: #333333 !important;
}

}
