css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("workflowy.com") {




BODY
{
	padding: 0;
	margin: 0;
	font-size: 18px;
	position: relative;
	font-family: Times;
	color: #FFFFFF !important;
	background: #1e1e1e !important;
}


.highlight
{
	height: 100%;
	left: -5px;
	padding: 2px 5px 1px;
	position: absolute;
	top: 2px;
	width: 100%;
}
.moving>.highlight, .highlighted>.highlight
{
	background: #1e1e1e !important;
}
.moving>.highlight, .highlighted.moveHovered>.highlight
{
	background: #1e1e1e !important;
}

.noteEditor > textarea{  
	padding: 0;
	margin: 0;
	font-size: 18px;
	position: relative;
	font-family: Times;
	color: #66d9ef !important;
	background: #272822 !important;    
}


.notes > .content{
    color: #66d9ef !important;
}

.page {
    max-width:1000px !important;
    position:relative;
    background: #272822 !important;

}


.parent > .name > .content
{
    color: #b92659 !important;
}





a.bullet{
    color: #b92659 !important;
}





span.plus{
    color: #cfcfcf !important;
}

span.text{
    color: #cfcfcf !important;
}


div.top.edge{
    display: none;
}

div.bottom.edge{
    display: none;
}

div.left.edge{
    display: none;
}

div.right.edge{
    display: none;
}

DIV.top.right.corner
{
    display: none;
}

DIV.top.left.corner
{
    display: none;
}

DIV.bottom.right.corner
{
    display: none;
}

DIV.bottom.left.corner
{
    display: none;
}
div#share_buttons{
    display: none;
}

}
