css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twitch.tv"), domain("twitchtv.com") {

/* [Main Column Size] */
.c12.clearfix.light, .c12.clearfix.dark, .c12.clearfix.wrapper {width:1305px !important;}

/*[Flash Player Size]*/
.live_site_player_container, #live_site_player_flash
{
  width: 900px !important;
  height: 550px !important;
}

.archive_site_player_container, #archive_site_player_flash
{
  width: 900px !important;
  height: 550px !important;
}


/* [szare tlo] */
div{
background-color: #2d2d2d !important;
color: #0f6b77 !important;
}		

/* [Chat] */
#chat {width: 400px !important; overflow:visible !important;}
#chat_column {width: 400px !important; margin-left:255px !important; margin-top: 6px !important;}
#chat_lines {overflow:auto !important; width: 400px !important; height:704px !important;}
#chat_line_list {padding-left: 5px !important; width: 375px !important; font: 1.05em/1.5"Lucida Grande","Lucida Sans Unicode","Lucida Sans",Geneva,Verdana,sans-serif !important;}
#jtv_chat {width: 400px !important;}

/* [Ads] */
.a300, .managed_ad {display:none !important;}

}
