css:
@-moz-document domain("badcamp07.org"), domain("vp7.com") {
td[rowspan="2"], #ad1, #ad2, .a2a_dd, #adx_zone_18885, .addthis_toolbox.addthis_default_style, strong[style="float:left;"], a[href="#latest"], a[href="#about"] {display:none!important;}

center+center>table[width="920"] {display:none!important;}

div[style="width:600px; float:left; margin-left:90px; text-align:center;"] {
width: 900px!important; margin: 10px 0 10px 80px!important;}

.result-thumb, img {border:none!important;}
.top-left, .top-right, .bottom-left, .bottom-right {background:none!important;}

.other {color: #fff;}
.other h1 {color:#111; text-transform: capitalize;}
#searchPage .result-body {margin: 10px;}
#logosearchBox + br + div {padding: 3px;}


#pagination {padding: 20px 0;}
.pageingLink, .pageingCurrent {padding: 3px 4px!important;}
.pageingLink {background:white; border-color:#BFBFBF; color:#666!important; margin-left:2px;}
.pageingLink:hover {border-color: #BA0000!important; text-decoration: underline!important; color:#555!important;}
.pageingCurrent {border-color: #0075B8!important; background:white!important; color:#666!important;}

.thumbs .ti li {max-width: 200px!important;}
.thumbs .ti * {line-height:17px;}

input[type="radio"] {-webkit-appearance: checkbox;}

#footer {color: #fff!important;}
#footer .bottomTxt {color: #222!important;}

}

@-moz-document domain("badcamp07.org") {
td[style="vertical-align:top;"]+td {display:block!important; padding-left: 76px;}
td{display:none;}


}
