css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(reddit.com) {
td.topmenu, div#topstrip, .arrow, td.numbercol, #infobar, div.pbox, span.little, div.sortbox, div#footer, #right {display: none!important;}
table#topbar {margin-bottom: 35px!important;}
tr.oddRow, tr.evenRow {height: 20px!important;}
p.menu {line-height:30px!important; color:#000!important; padding-bottom:10px!important;}
a:link.title {color: #03a!important; font-weight:bold!important; font-size:13px!important; font-family : "DejaVu Sans", Verdana, Helvetica, sans-serif!important; letter-spacing: 1px!important;}
a:visited.title {color: #999!important; font-weight: normal!important; font-size:13px!important; font-family : "DejaVu Sans", Verdana, Helvetica, sans-serif!important; letter-spacing: 1px!important;}
a:hover.title {color: #900!important; font-size:13px!important; font-family : "DejaVu Sans", Verdana, Helvetica, sans-serif!important; letter-spacing: 1px!important;}
a:active.title {color: #0f0!important; font-size:13px!important; font-family : "DejaVu Sans", Verdana, Helvetica, sans-serif!important; letter-spacing: 1px!important;}
}
