css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("perezhilton.com") {

/* hides advertisements
   and fixes the layout */
#content > table tr:first-child,
#left_skyscraper,
img[src*="advertise"],
img[src*="premium"],
img[src*="represent"],
a[href*="merchdirect.net/perezhilton"],
div[id*="content_ad"],
#google_ads,
.adspot {
display: none !important;
}

#header {
margin-bottom: 10px !important;
}

#content > table td[width="200"] br {
font-size: 0.05em !important;
}

.post {
margin-bottom: 10px !important;
}

/* hides advertisement backgrounds (if any)
   and fixes the layout */
body,
#forbg {
background: #FBCCCD none !important;
}

.sidebarcopy,
#footer,
.pages_links,
.alignright,
.alignleft {
background: #FBCCCD none !important;
}

a {
background: transparent !important;
}

}
