css:
@-moz-document url-prefix("http://www.radiorecord.ru/player/") {

.rotator2, .dragger_container, .scrollUpBtn, .scrollDownBtn
{display: none !important;}

html, body {
height: 100% !important;
width: 100% !important;}

div.player div.right, #mcs_container, #mcs_container .customScrollBox .container, div.player div.right div.stations
{width: 200px !important;}

div.player div.right div.stations ul li
{padding: 4px 0px !important;}

}
