@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

.tabbrowser-tab[pending], .alltabs-item[pending] {
	/*optional to change transparency: opacity: .7 !important;*/
	-moz-border-image: url("data:image/png;base64,") 4 10 3 3 / 4px 10px 0px 3px stretch !important;
	border-radius: 1px 18px 0px 0px !important;
}

.tabbrowser-tab[pending] .tab-icon-image, .alltabs-item[pending] .tab-icon-image {
	filter: url("chrome://mozapps/skin/extensions/extensions.svg#greyscale");
}
