css:
@-moz-document url-prefix("http://sguforums.com/") {
blockquote, blockquote blockquote blockquote{
font-size: 11px;
padding: 5px 5px 5px 5px;
border-top: none;
}

img.avatar {
height: auto;
margin-left: auto;
margin-right: auto;
}

div#wrapper {
width: auto;
max-width: 1400px;
}

#header {
background-repeat: no-repeat;
}

.bbc_alternate_quote {
background: #182037;
}

.quoteheader {
background: #242434;
border-top: solid 1px black;
}

div.spoilerbody {
color: #FFFFFa;
}

h4.catbg, h4.catbg2, h3.catbg, h3.catbg2, .table_list tbody.header td.catbg {
background: none;
}

div.cat_bar {
background-repeat: repeat-x;
}

.quickReplyContent textarea {
resize:vertical;
width: 100%;
overflow: auto;
}
}
