css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://www.google.com/calendar"),
                              url-prefix("https://www.google.com/calendar"),
url-prefix(javascript:parent.gtasks.embed.createHtmlForTasksFrame) {
    /* Hide the control bar that allows you to hide/show tasks frame */
    #rhstogglecell { display: none !important;}

    /* Resize main calendar cell, by enlarging the right margin */
    #maincell { margin-right: 290px !important; }

    /* Enlarge task frame */
    #gadgetcell { width: 289px !important; }

    /* Enlarge task frame contents */
    #gadgetcell .sn-frame { width: 289px !important; }

    #gadgetcell.gadgetcell-closed { width: 0px ! important; }
    #gadgetcell.gadgetcell-closed .sn-frame { width: 0px !important; }

    /* Resize the Notes field in the Task Editor */ 
    textarea[class="j ec L"] {  min-height: 400px; }
    textarea[class="j ec"] {  min-height: 400px; }
}
