css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("barnesandnoble.com")

{

/*text&background*/

html, body, tbody, thead, th, tr, td, blockquote, li, ul, h1, h2, h3, h4, h5, h6, font, strong, p, form, footer

{
color: white !important;
background: black !important;
}

select, button, div#bnf-footlight-cnt1, #bnf-footlight-cnt2, #bnf-footlight-ctr, div#bnf-pods div, div#forceWidth, div#home-left-bottom, div.carousel-applier, div.body, div.body div, div.bn-global-header-container, ul#bn-nav-global.x > li > a:hover, ul#bn-nav-global.x > li > a:link, div.three-col, ul.nav li div a

{
background: black !important;
}

span

{
background-color: black !important;
}

select, input, span, code, textarea, pre, label, div, dd

{
color: white !important;
}


/*links*/

a

{
color: #0040ff !important;
}

a:visited

{
color: #224375 !important;
}

/*highlight*/

textarea, pre, input, code

{
background: #333333 !important;
}

}
