css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mymessagesafe.com") {

		.logo,
		img[src*="header_"],
		img[src*="footer"],
		div[style*="dotted #aaaaaa"],
		td.nav_background span,
		div[style*="666666"]
		{display:none !important;}
		
		td.nav_background {
			display:none;
		}
		#my_menu {
			width:100%;
			margin:0;
			padding:0;
			border:0;
		}
		#my_menu td {
			border:0;
			padding-bottom:0;
		}
		.nav_border {
			display:none;
		}
		
		td.nav_background tr {
			display:block;
			float:left;
		}

		form table tr {
			display:table-row;
		}
		
		table.nav {

		}
		
		.footer {
			display:none;
		}
		
		td {

			text-overflow:ellipses;

		}

}

@-moz-document url("https://www.mymessagesafe.com/"), url("http://www.mymessagesafe.com/index/"), url("https://www.mymessagesafe.com/index/") {

/* home page */

#main_cont,
a[href="/howitworks/"],
a[href="/register/"],
.background_right,
.background_left {
	display:none !important;
}
form[action="/login/"] {
	position: absolute;
	left: 50%;
	top: 50%;
	margin: -90px 0 0 -90px;
}
body {
	background-color:#FFF;
}

}
