css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("en.wikipedia.org") {
DIV#mw-sopaOverlay {
  display: none;
}
#mw-page-base, #mw-head-base, #content, #mw-head, #mw-panel, #footer {
  display: inherit !important;
}
}

@-moz-document domain("wordpress.com") {
#editorpicks a:hover span.posttitle,
#editorpicks span.posttitle,
#editorpicks small span,
#editorpicks span.freshly *,
#editorpicks span.thebestof *,
#editorpicks a.tagazine{
  color: inherit !important;
  background: inherit !important;
  background-color: inherit !important;
}
#editorpicks a.tagazine,
#editorpicks a.sub-button {
  display: block !important;
}

#editorpicks .picture{
  border-color: inherit !important;
  overflow: inherit !important;
  background-color: inherit !important ;
  background-image: inherit !important;
  background-position: center !important;
  background-repeat: inherit !important;
}

#editorpicks span.thebestof span{
  display:inline;
}

#editorpicks span.thebestof *{
  font-size: inherit !important;
  font-family: inherit !important;
}
}
