css:
@-moz-document domain("nexusmods.com") {
#top {
position: fixed;
padding-left: 50%;
z-index: 9001;
}
#top-inner {
margin-left: -50%;
}
#subnav {
position: fixed;
z-index: 9002;
}
#main, #content, .highslide-image {
padding-top: 125px !important;
}
#horizontal-ad {
  display: none;
}
}

@-moz-document domain("www.nexusmods.com") {
#top {
position: relative;
padding-left: 0;
}
#main, #content {
padding-top: 0;
}
}
