css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://www.gnpcb.org/esv/mobile) {

html, body {
	background: #fefefe;
	color: #444;
}
body {
	font-size: 112%;
	max-width: 33em;
	margin: 0 auto;
	width: 33em;
}
#reading-day-nav, #todays-readings, .top-link {
	display: none;
}
body, .devotion, .devotion div.esv-text, .devotion div.chunk.esv-text {
	font-family: "Hoefler Text", Constantia, "Lucida Bright", Lucidabright, "Lucida Serif", Lucida, "DejaVu Serif", "Bitstream Vera Serif", "Liberation Serif", Georgia, serif;
	line-height: 1.5em;
}
.excerpted-from, .footnote, .footnotes, .noprint {
	border: none;
	font-size: 90%;
	margin: 0;
}
a {
	color: #e22;
	font-size: 80%;
}
h2 {
	font-variant: small-caps;
	font-weight: normal;
	letter-spacing: 0.05em;
}
h2:first-child {
	margin: 3em 0 2em;
	text-align: center;
}
h2:first-child:after { 
	color: #e22;
	content: "†";
	display: block;
	margin-top: 1em;
}
h3 {
	font-weight: normal;
	font-style: italic;
}
p {
	margin-bottom: 0;
}
p + p {
	margin-top: 0;
}
p + p, .block-indent + p {
	text-indent: 2em;
}
p.line-group, .block-indent + p.same-paragraph {
	text-indent: 0;
	margin-top: 1em;
	margin-bottom: 0;
}
.block-indent {
	margin-left: 3em;
}
.speaker {
	font-style: italic;
	margin-left: -3em;
}
.footnote a, .footnotes {
	font-style: italic;
	text-decoration: none !important;
}
.footnotes:before {
	color: #e22;
	content: "¶";
	display: block;
	font-size: 120%;
	font-style: normal;
	margin: 1em auto;
	text-align: center;
}
.footnotes h3 {
	font-size: 120%;
}
.excerpted-from {
	margin-top: 3em;
	margin-bottom: 2em;
	text-indent: 0;
}
.excerpted-from:before {
	color: #e22;
	content: "❦";
	display: block;
	font-size: 150%;
	font-style: normal;
	margin: 1em auto;
	text-align: center;
}
/* Optional */
form, .chapter-num, .verse-num, .noprint, .surround {
	display: none;
}
}
