css:
/*Now it looks like a ticker without that many space between the lines
and readable font*/
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://www.golem.de/ticker/"){
*{font-size: 15px !important;}
*{line-height: 10pt !important;}
body {
  background-position:center top !important;
  margin: 0px auto !important;
  width: 930px !important;
  float: none !important;
}
#adspace, #sky, #big, #dfpsky {
  display:none !important;
}
.g4 .list-tickers {
	margin:10px 0 !important;
	line-height: 1pt !important;
}
.g4 h2 {
	margin:10px 0 !important;
	line-height: 10pt !important;
}
.g4 .list-tickers h3 a {
 	margin:10px 0 !important;
 	color:#222222;
 	line-height: 10pt !important;
}
.g4 .list-tickers h3 a strong {
 	color:#254EAD;
 	margin:10px 0 !important;
 	line-height: 10pt !important;
}
.g4 .list-tickers h3 a:visited strong {
	color:#222222;
	margin:10px 0 !important;
	line-height: 10pt !important;
}
}
@-moz-document domain("scr3.golem.de") {
body {
  margin: 0px auto !important;
  width: 100% !important;
  float: none !important;
  }

}
