css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://translate.twttr.com/") {
}
.input {
direction: rtl;
text-align: right;
}
textarea {
direction: rtl;
text-align: right
}
div.vote span {
direction: rtl;
text-align: right;
}
div.vote {
background-position: right center !important;
max-width: 453px !important;
width: 453px !important;
}
div.vote:hover {
background-position: right center !important;
}
.tl-translation {
direction: rtl;
text-align: right;
}
.tl-translation strong {
float: right;
}
div.permalink div.permalink-header div.permalink-votes ul li span {
direction: rtl;
text-align: right;
}
.dsq-comment-message {
direction: rtl;
text-align: right;
}
#comment {
direction: rtl;
text-align: right;
}
div.permalink div.permalink-header div.permalink-votes ul li {
float: right !important;
}
.approved {
direction: rtl;
text-align: right;
}
.translation-in-use {
direction: rtl;
text-align: right;
border-right: 0px solid #CCCCCC !important;
border-left: 1px solid #CCCCCC !important;
float: right !important;
}
