css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(delphiforums.com) {

#logo a img {
	display: none;
}

#header #logo a {
	background-image: url(http://www.angehowe.com/delphi/DFLogo-sm.png) !important;
	background-position: 0 0 !important;
	width: 99px !important;
	height: 44px !important;
}
 
body {
	margin: 0px;
	padding: 0px;
	border: 4px !important;
	font-family: Verdana, Arial, Helvetica, sans-serif;
	font-size: 100%;
	line-height: 15px;
	color: #dcdcdc !important;
}

#header a {
	color: #e0ffff !important;
	text-decoration: none;
}

#header {
	background: url(http://www.angehowe.com/delphi/blackFade.png) #000000 repeat-x 100% 100% !important;
	height: 52px;
	width: 100%;
	float: left;
	font-size: 11px;
	border: 0px;
	margin: 0px;
	padding: 0px;
}

#header p {
	margin: 0px;
}

#header #logo {
	width: 100px;
	margin: 6px 15pxt;
	height: 0px!important;
	border: none;
	float: left;
}

#header #user {
	height: 38px;
	margin: 6px 0px 8px 0px;
	padding-left: 15px;
	float: left;
	width: 240px;
	border-left: 1px solid #add8e6 !important;
}

#header #user .topLine {
	line-height: 20px;
}

#header #user .bottomLine {
	height: 12px;
	line-height: 12px;
}

#header #user a.memberName {
	font-size: 10px;
	color: #add8e6;
	font-family: Tahoma, Verdana, Arial, Helvetica, sans-serif;
	height: 20px;
	padding-top: 8px;
}

#header #user a.loginout {
	margin-top: 4px;
}

#header #messages {
	line-height: 18px;
	height: 38px;
	margin: 6px 0px 8px 0px;
	width: 350px;
	float: right;
	border-right: 1px solid #add8e6 !important;
	border-left: 1px solid #add8e6 !important;
	padding-left: 4px;
	padding-right: 12px;
}

#header #messages .topLine {
	margin: 0px 0px 2px 0px;
}

#header #messages span.msgCnt {
	width: 22px;
	text-align: right;
	margin-right: 6px;
	font-family: Arial, Helvetica, sans-serif;
	float: left;
}

#header #messages .newMessages {
	color: #add8e6 !important;
	font-weight: bold;
}

#header #messages .noMessages {
	color: #add8e6 !important;
}

#header #messages span.toMe {
	margin-left: .4em;
}

#header #messages a.btnRight {
	float: right;
	text-align: right;
}

#header #messages a, #header #messages span {
	height: 18px;
	vertical-align: bottom;
}

#header #buttons {
	height: 48px;
	float: right;
	margin-right: 0px;
	margin-left: 1px;
	font-family: Arial, Helvetica, sans-serif;
	font-size: 8px;
	text-align: center;
	width: 216px;
}

#header #buttons a {
	color: #666666;
	width: 52px;
	height: 13px;
	float: right;
	padding-top: 35px;
	line-height: 12px;
	overflow: hidden;
	white-space: nowrap;
}

#logo a img {
	display: none;
}

#header #logo {
	background-image: url(http://www.angehowe.com/delphi/DFLogo-sm.png) !important;
	background-position: 50% 50% !important;
	background-repeat: no-repeat;
	width: 99px !important;
	height: 44px !important;
}

#header #buttons a#help {
	background-color: transparent !important;
	background-image: url(http://www.angehowe.com/delphi/Help-gray.png) !important;
}

#header #buttons a#help:hover {
	background-color: transparent !important;
	background-image: url(http://www.angehowe.com/delphi/Help-blue.png) !important;
}

#header #buttons a#chat {
	background-color: transparent !important;
	background-image: url(http://www.angehowe.com/delphi/Chat-gray.png) !important;
}

#header #buttons a#chat:hover {
	background-color: transparent !important;
	background-image: url(http://www.angehowe.com/delphi/Chat-blue.png) !important;
}

#header #buttons a#allForums {
	background-color: transparent !important;
	background-image: url(http://www.angehowe.com/delphi/Forums-gray.png) !important;
}

#header #buttons a#allForums:hover {
	background-color: transparent !important;
	background-image: url(http://www.angehowe.com/delphi/Forums-blue.png) !important;
}

#header #buttons a#myForums {
	background-color: transparent !important;
	background-image: url(http://www.angehowe.com/delphi/MyForums-gray.png) !important;
}

#header #buttons a#myForums:hover {
	background-color: transparent !important;
	background-image: url(http://www.angehowe.com/delphi/MyForums-blue.png) !important;
}

}
