css:
@-moz-document domain("trello.com") {


body {
  font-family: 'Trebuchet MS', Helvetica, Arial, sans-serif;
}
.badge {
  font-family: 'Trebuchet MS', Helvetica, Arial, sans-serif;
}
body, input, select, textarea {
  font-size: 1em;
}
.button-link {
  font-size: 0.8em;
}
.list-title h2, .board-widget-title h3, .window-title h2, textarea.field.single-line, textarea.new-card {
  font-size: 0.8em;
}
div.phenom-desc, div.new-comment {
  font-size: 0.75em;
}
a.list-card-title {
  font-size: 0.75em;
  line-height: 1.3em;
}
.board-list-actions {
  font-size: 0.7em;
  line-height: 1.2em;
}
.window-main-col-content {
  font-size: 1em;
  line-height: 1.4em;
}
.new-comment textarea {
  font-family: 'Trebuchet MS', Helvetica, Arial, sans-serif;
  font-size: 1.1em;
}

}
