css:
@-moz-document domain("hup.hu") {

a.comment_add,
a.comment_comments,
a.comment_new_comments,
a.node_read_more,
a.blog_usernames_blog {
display:inline-block;
padding:2px 5px 3px 5px;
color:#444 !important;
text-decoration:none !important;
-moz-border-radius:3px;
border: 1px solid #999;
background: -moz-linear-gradient(top, #eee, #aaa);
line-height: 100%;
}

a.comment_add:hover,
a.comment_comments:hover,
a.comment_new_comments:hover,
a.node_read_more:hover,
a.blog_usernames_blog:hover {
background: -moz-linear-gradient(top, #eee, #eaba8b);
text-decoration: none !important;}

a.comment_add:active {
background: -moz-linear-gradient(top, #aaa, #eee);
text-decoration: none !important;}

a.comment_add:after {
content: " »";
}

a.comment_reply {
display:inline-block;
padding:2px 5px 2px 5px;
background:#c6c6b3;
color:#000 !important;
text-decoration:none !important;
}
a.comment_reply:hover {background:#f8b879;text-decoration: none !important;}
a.comment_reply:after {
content: " »";
}

html *{outline:none !important; -moz-outline:none !important;}

div.links {
background: #e7e7da !important;
padding-top: 3px;
padding-bottom: 3px;
-moz-border-radius:3px;
}

.sticky div.links {
background: #ddd !important;
padding-top: 3px;
padding-bottom: 3px;
-moz-border-radius:3px;
}

span.statistics_counter {display:none;}

.comment, .comment-new {
    min-width: 300px;
}

}
