css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("workflowy.com") {

/* Reduce font size */
.selected .project > .name > .content, .nameEditor > textarea {
	font-size: 10pt;
}

/* Maintain heading font size */
.selectedEditor > textarea { font-size: 26px }

/* Hide stuff */
#siteSlogan, #share_buttons, #bottomLinks { visibility: hidden }
#site_message { display: none }

#header { min-width: 670px }

#visible {
	width: 70%;
	padding: 0 0 0 100px;
	margin: 0;
}

/* Remove borders */
.top, .bottom, .left, .right { display: none }

/* Reduce minimum height */
#workflowy, #visible {
	min-height: 400px;
}

/* Hide "Get more space" */
.getMoreSpaceButton {
	display: none;
}

/* Hide footer */
.footer {
	display: none;
}

/* Hide "Go Pro" button */
.proPitch {
	display: none;
}

/* Float link right */
#exportAll { float: right }

/* Add row highlights (obsolete) */
/*
.editor:hover {
	border-radius: 2px;
	background-color: #fafafa;
	opacity: 1;
}
*/

/* Fix expand/collapse alignment */
.project > .name #controls #expandButton .expandPlus {
	display: inline-block;
	position: relative;
	top: 2px;
}

.project > .name #controls #expandButton .expandMinus {
	top: -7px;
}

/* Hide occasional bottom scrollbar */
body {
	overflow-x: hidden;
}

/* Remove Facebook Like button */
.facebookLike {
	display: none;
}

/* Remove Twitter button */
.twitter-follow-button {
	display: none;
}

}
