css:
@-moz-document domain("fitocracy.com") {
/* bigger and more colored notifications */
#global_notification_bubble > div {
color: #1ea7db !important;
font-size: 20px !important;
line-height: 20px !important;
min-width: 19px !important;
}

#global_notification_bubble {
margin: 5px 0 0 0 !important;
}

.uprightnotch, .downrightnotch {
right: 27px !important;
}

/* colored workout notes */
ul.action_detail li.stream_note {
color: #993333 !important;
}

/* changes to the props row */
a.gray_link, a.gray_icon_link {
font-size: 16px !important;
}

a.gray_link:hover, a.gray_icon_link:hover { 
color: #6f94db !important;
}
/* bigger profile pictures */
.avatar.large {
height: 300px !important;
width: 300px !important;
}

/* smaller author links, so long names no longer wrap */
.stream-author {
font-size: 14px !important;
}

/* more noticeable PRs */
ul.action_detail li.pr {
font-weight:bold !important;
}

/* more space for commenting */
.user_comment textarea {
height: 50px !important;
max-height: 180px !important;
}

.avatar.small {
margin-bottom: 5px !important;
}
}
