css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("nasdaq.com")

{

/*text&background*/

html, body, tbody, thead, th, tr, td, blockquote, li, ul, h1, h2, h3, h4, h5, h6, font, strong, p, form, footer

{
color: white !important;
background: black !important;
}

select, button, #panel_content_ads, #quick-find, #liMarket-span, .button span, div#left div a.button, #main-nav li a span, #top_qw, .go-buttons_qw, #quotenavfooter, #careerspromo, .search-social-box, .register-login, #quotesnav ul li a, #quotesnav ul li a:hover 

{
background: black !important;
}

div, span, a

{
background-color: black !important;
}

select, input, span, code, textarea, pre, label, div, dd, option

{
color: white !important;
}


/*links*/

a

{
color: #0040ff !important;
}

a:visited

{
color: #224375 !important;
}

/*highlight*/

textarea, pre, code

{
background: #333333 !important;
}

}
