css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lotro.com") {

    .header_border, .basic-tooltip, .bbcode_container div.bbcode_quote, .sticky, .forumbit_nopost .forumbit_nopost .forumrow, .forumbit_post .forumrow, .nonsticky, .threadbit .deleted, .forum_info_block, .forum_info_form, #announcement_box > DIV:last-child, .postbit .postuseravatarlink img, .thread_info_block, .postbit, .postbitlegacy, .eventbit, .postbit .userinfo, .postbit .userinfo_noavatar, #above_threadlist_controls, #pagetitle, #above_threadlist, #above_postlist, .thread_controls, .navlinks, #below_postlist, #below_threadlist, #postlist, .forumfoot, .blockhead, .section, .blockbody.formcontrols, .blockrow.floatcontainer {
      border-color: transparent !important; /* forum outline */
    }
    
    .postbit.blockrow {
     border-color: #dadada !important;
     }
    
    .searchlisthead, a.textcontrol, .popupctrl {
     background-color: transparent !important;
     border-color: transparent !important; /* forum outline */
    }
    
    .signature {
     text-align: center;
    }
    
    span.forumtitle {
     font-size: 20px !important;
    }

    h1 {
     font-size: 16px !important;
    }

    h2 {
     font-size: 14px !important;
    }

    h3.threadtitle {
     font-size: 14px !important;
    }

    div#breadcrumbs_content {
     font-size: 12px !important;
    }
	
	div.threadmeta, ul.threadstats.td.alt, dl.threadlastpost.td, ul.forumstats.td, div.forumlastpost.td {
     font-size: 9px !important; /* statistics text */
    }
}
