css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("craigslist.org") {
  body { font-family: Arial; color: #000000; }
  table { text-align: center; background-color: #33CC00 !important; width:100%;}
  #topban div { background: #009900 !important;}
  a { color: #000000 !important; }
  table.city a:hover { color: #FFFFFF !important; }
  body.hp { background: #009900 !important;}
  body.hp a:hover { color: #000000; }
  body.toc { background: #009900 !important;}
  table.city { letter-spacing: 1px !important; background: #33CC00 !important; }
  table.city b { letter-spacing: 2px !important; background: #33CC00 !important; color: #FFFFFF !important; }
  td#leftbar { background: #009900 !important; }
  div#logo { letter-spacing: 1.5px; background: #009900 !important; }
  ul#postlks { background: #33CC00 !important; }
  ul#postlks a:hover {color: #000000 !important; }
  td#topban { background: #009900 !important; }
  table { background: #33CC00 !important; }
  td#leftbar div { background: #009900 !important; color: #FFFFFF !important; letter-spacing: 1.5px !important; }
  #postlks a:link { color: #FFFFFF !important; }
  i:before { content: "~~~~~~~~~~ [ "; }
  i:after { content: " ]"; }
  p:before { content: " - "; }
  .ban { background: #33FF33 !important;}
}
